import React from "react";
import classes from "./Footer.module.scss";
import { Container, Row, Col } from "react-bootstrap";
import { Logo } from "../";
import FooterItems from "./FooterItems/FooterItems";
const footer = () => {
	return (
		<footer className={classes.Footer}>
			<Container>
				<Row>
					<Col sm={4}>
						<Logo />
					</Col>
				</Row>
				<Row className="pt-2 pb-4">
					<Col sm={4}>
						<div className="d-flex justify-content-between align-items-start">
							<div>
								<p className="mb-2 text-small">+998 90 000 00 00</p>
								<p className="mb-2 text-small">+998 90 000 00 00</p>
								<p className="mb-2 text-small">Служба поддержки</p>
							</div>
							<div>
								<h6 className="text-accent">Оплата</h6>
								<ul className={classes.FlexColumns}>
									{getImages().map((image, index) => {
										return (
											<li key={index}>
												<img src={image} />
											</li>
										);
									})}
								</ul>
							</div>
						</div>
					</Col>
					<Col sm={{ span: 7, offset: 1 }}>
						<div className="d-flex justify-content-between align-items-start w-100 text-left">
							{getItems().map(item => {
								return <FooterItems key={item.title} title={item.title} items={item.items} />;
							})}
						</div>
					</Col>
				</Row>
			</Container>
		</footer>
	);
};
const getImages = () => [
	"/static/images/payment/payme.png",
	"/static/images/payment/uzcard.png",
	"/static/images/payment/click.png",
	"/static/images/payment/humo.png",
	"/static/images/payment/visa.png",
	"/static/images/payment/paynet.png"
];
const getItems = () => [
	{
		title: "О нас",
		items: [
			{
				title: "Часто задаваемые вопросы",
				link: "/"
			},
			{
				title: "Помощь",
				link: "/"
			},
			{
				title: "Правила и условия",
				link: "/"
			},
			{
				title: "Электронный билет",
				link: "/"
			},
			{
				title: "Возврат и обмен",
				link: "/"
			},
			{
				title: "Политика конфиденциальности",
				link: "/"
			}
		]
	},
	{
		title: "Сотрудничество",
		items: [
			{
				title: "Издательствам",
				link: "/"
			},
			{
				title: "Авторам",
				link: "/"
			},
			{
				title: "Библиотекам",
				link: "/"
			},
			{
				title: "Партнёрам",
				link: "/"
			},
			{
				title: "Стать спикером",
				link: "/"
			}
		]
	},
	{
		title: "Что почитать?",
		items: [
			{
				title: "Бестселлеры",
				link: "/"
			},
			{
				title: "Скоро в продаже",
				link: "/"
			},
			{
				title: "Публичная оферта",
				link: "/"
			},
			{
				title: "Конфиденциальность",
				link: "/"
			}
		]
	}
];
export default footer;
